class Solution:
    def threeSum(self, nums):

        nums.sort()
        triplets = []

        seens_i = {}
        # [-4, -1, -1, 0, 1, 2]
        for i in range(len(nums)):
            if nums[i] > 0:
                break

            if nums[i] in seens_i:
                continue
            else:
                seens_i[nums[i]] = i

            target = 0 - nums[i]
            seens = {}
            for j in range(i+1, len(nums)):
                diff = target - nums[j]

                if diff in seens:
                    triple = [nums[i], diff, nums[j]]
                    if triple not in triplets: 
                        triplets.append(triple)

                if nums[j] not in seens:
                    seens[nums[j]] = j
        
        return triplets

# Test Cases

